  return (
  <p>
    {props.voltas} <br/>
    <span>Voltas</span>
  </p>
  )
}

const MostraTempo = (props)=>{
  return(
    <p>
      {props.tempo} <br/>
      <span>Tempo m√©dio por volta</span>
    </p>
  )
}

const Button = (props)=> <button>{props.text}</button>

function App() {

  let numVoltas = 14
  const increment = ()=>{
    numVoltas++
  }
  const Deincrement = ()=>{
    numVoltas--
  }


  return (
    <div className="App">
      <MostraVoltas voltas ={numVoltas}/>
      <Button text = '+'/>
      <Button text = '-'/>


      <MostraTempo tempo='01:30' />
      <Button text = 'Iniciar'/>
      <Button text = 'Reiniciar'/>

    </div>
  );
}

export default App;
