import {useState, useEffect} from 'react'

const MostraVoltas = (props)=>{
  return (
  <p>
    {props.voltas} <br/>
    <span>Voltas</span>
  </p>
  )
}

const MostraTempo = (props)=>{
  const tempo = props.tempo
  const minutos = Math.round(tempo/60)
  const segundos = tempo%60;

  const segundosStr = segundos < 10 ? '0' + segundos: segundos
  const minutosStr = minutos < 10 ? '0' + minutos: minutos

  return(
    <p>
      {`${minutosStr}:${segundosStr}`} <br/>
      <span>Tempo médio por volta</span>
    </p>
  )
}

const Button = (props)=> <button onClick={props.onClick}>{props.text}</button>

function App() {

  const [numVoltas,setNumVoltas] = useState(0)
  const [running, setRunning] =  useState(false)
  const [tempo,setTempo] = useState(0)

  useEffect(()=>{

    let timer = null
    if(running){

      timer = setInterval(()=>{
        setTempo(old => old +1)
      },500)
    }
    return ()=>{
      if(timer){ // vai retornar um truthy, valor que é convertido para verdadeiro
        clearInterval(timer)
      }
    }
  },[running]) //Ao passar o colchetes, ele so vai ser chamado na primeira vez

  const toggleRunning=()=>{
    setRunning(!running)
  }

  const increment = ()=>{
    setNumVoltas(numVoltas+1)

  }

  const decrement = ()=>{
    setNumVoltas(numVoltas-1)
  }

  const reset = ()=>{
    // setNumVoltas = (0)
    setTempo = (0) 
  }


  return (
    <div className="App">
      <MostraVoltas voltas ={numVoltas}/>
      <Button text = '+' onClick = {increment}/>
      <Button text = '-' onClick={decrement}/>

      {
        numVoltas > 0 &&
        <MostraTempo tempo={Math.round(tempo/numVoltas)} />
      }
      <Button onClick={toggleRunning} text = 'Iniciar'/>
      <Button onClick ={reset} text = 'Reiniciar'/>

    </div>
  );
}

export default App;
